    private void CirculoBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CirculoBTActionPerformed
        
        double ladocirculo = Double.parseDouble( LadoTF.getText());        
        Triangulo t1 = new Triangulo(ladocirculo);        
        PerimetroTF.setText(String.valueOf(t1.perimetro()));
        AlturaTF.setText(String.valueOf(t1.altura()));
        AreaTF.setText(String.valueOf(t1.area(t1.altura())));
        
        
            
            
            
            
            
            
            
        

    }//GEN-LAST:event_CirculoBTActionPerformed

