    private void cuadradoBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cuadradoBTActionPerformed
        int lado_cuadrado = Integer.parseInt( String.valueOf(ladoCuadradoTF.getText()));
            Cuadrado cu = new Cuadrado(lado_cuadrado);
            AreaCuadradoTF.setText(String.valueOf(cu.calcularArea()));
            PerimetroCuadradoTF.setText(String.valueOf(cu.calcularPerímetro()));
    }//GEN-LAST:event_cuadradoBTActionPerformed

    private void rectanguloBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rectanguloBTActionPerformed
        int baseR = Integer.parseInt( String.valueOf(baseRTF.getText()));
            int alturaR = Integer.parseInt( String.valueOf(alturaRTF.getText()));
            
            Rectángulo r1 = new Rectángulo(baseR, alturaR);
            AreaRectanguloTF.setText(String.valueOf(r1.calcularArea()));
            PerimetroRectanguloTF.setText(String.valueOf(r1.calcularPerímetro()));
    }//GEN-LAST:event_rectanguloBTActionPerformed

    private void trBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_trBTActionPerformed
        int baseTR = Integer.parseInt( String.valueOf(basetrTF.getText()));
            int alturaTR = Integer.parseInt( String.valueOf(alturatrTF.getText()));
            
            TriánguloRectángulo tr1 = new TriánguloRectángulo(baseTR, alturaTR);
            areatrTF.setText(String.valueOf(tr1.calcularArea()));
            perimetrotrTF.setText(String.valueOf(tr1.calcularPerímetro()));
            hipotenusaTF.setText(String.valueOf(tr1.calcularHipotenusa()));
            tipoTrianguloTF.setText(String.valueOf(tr1.determinarTipoTriángulo()));
    }//GEN-LAST:event_trBTActionPerformed

